mapper.select:
select distinct u.id, u.user_id, u.user_name, u.user_type, u.email, u.avatar, u.phonenumber, u.status, u.create_time from tb_system_user u left join tb_system_user_role ur on u.id = ur.user_id left join tb_system_role r on r.id = ur.role_id where u.del_flag = '0' and r.id = #{roleId}
AND u.user_id like concat('%', #{userId}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}
